      <label className="cep">
        <h1>
          Cep:
        </h1>
        <div>
          {addressCep.localidate}
        </div>
        <input
          type="text"
          value={cep}
          onChange={e => setCep(e.target.value)}
        />
      </label>
      <input type="submit" value="Submit" />
    </form>
  );
}

export default App;
